"use strict";

var headerSelect = document.querySelectorAll(".header__search-location");
headerSelect.forEach(function (el) {
  el.addEventListener("click", function () {
    var selectWrapper = el.closest(".header__search-box");
    var headerDropDown = selectWrapper.querySelector(".header__search-list-wrapper");
    var headerSelectItem = selectWrapper.querySelectorAll(".header__search-item");
    var headerSelectOutPut = selectWrapper.querySelector(".header__search-box-output");

    if (headerDropDown.style.height == "0px" || !headerDropDown.style.height) {
      headerDropDown.style.height = headerDropDown.scrollHeight + "px";
    } else {
      headerDropDown.style.height = 0;
    }

    headerSelectItem.forEach(function (selectItem) {
      selectItem.addEventListener("click", function () {
        headerSelectOutPut.innerHTML = selectItem.innerText;
      });
    });
    window.addEventListener("click", function (e) {
      if (e.target != el) headerDropDown.style.height = 0;
    });
  });
});
var body = document.querySelector("body");
var menu = document.querySelector(".nav__menu");
var navList = document.querySelector(".nav__list");
var menuOpen = false;
menu.addEventListener("click", function () {
  if (!menuOpen) {
    menu.classList.add("open");
    navList.classList.add("active");
    body.classList.add("active");
    menu.style = "z-index: 999;\n                        position:fixed;\n                        right: 5%; ";
    menuOpen = true;
  } else {
    menu.classList.remove("open");
    navList.classList.remove("active");
    body.classList.remove("active");
    menu.style = "position: absolute;";
    menuOpen = false;
  }
});
